<?php	include( "vars.php" );	function returnUnqTestimonial($num)	{		GLOBAL $TESTIMONIAL_TOTAL_NUMBER, $TESTIMONIAL_PATH;				$day_of_year = intval( date( "z" ) );		$_t = ( floor( $day_of_year/$TESTIMONIAL_TOTAL_NUMBER ) )*$TESTIMONIAL_TOTAL_NUMBER;		$_diff = ($day_of_year - $_t);				$ret = $num + $_diff;				if( $ret > $TESTIMONIAL_TOTAL_NUMBER )			$ret = ( $ret - $TESTIMONIAL_TOTAL_NUMBER );				$tetimonial_doc = $TESTIMONIAL_PATH.$ret.".html";				if( file_exists( $tetimonial_doc ) )			return( file_get_contents( $tetimonial_doc ) );		return( false );	}		function returnUnqTestimonialForBrain($TESTIMONIAL_TOTAL_NUMBER ,$TESTIMONIAL_PATH) //local vars not global	{		$day_of_year = intval( date( "z" ) );			$_t = ( floor( $day_of_year/$TESTIMONIAL_TOTAL_NUMBER ) )*$TESTIMONIAL_TOTAL_NUMBER;				$_diff = ($day_of_year - $_t);				$ret = $_diff+1; 	//New logic to autorotate between selectecd range here (1,2,3)				if( $ret > $TESTIMONIAL_TOTAL_NUMBER )			$ret = ( $ret - $TESTIMONIAL_TOTAL_NUMBER );				$tetimonial_doc = $TESTIMONIAL_PATH.$ret.".html";				if( file_exists( $tetimonial_doc ) )			return( file_get_contents( $tetimonial_doc ) );		return( false );	}?>